/**
 * Scroll bar visibility of a UIScrollView
 * Reference: https://docs.microsoft.com/en-us/dotnet/api/xamarin.forms.scrollbarvisibility
 */
export class ScrollBarVisibility {
    /** @returns {ScrollBarVisibility} */ static get ALWAYS(): ScrollBarVisibility;
    /** @returns {ScrollBarVisibility} */ static get DEFAULT(): ScrollBarVisibility;
    /** @returns {ScrollBarVisibility} */ static get NEVER(): ScrollBarVisibility;
}
